sys_script_include.script:
var DoNowUtil = Class.create();
DoNowUtil.prototype = {
	initialize: function() {
	},

	/*
	Description: 
	Return: 
	*/
	getMyDefaultContext : function() {

		var gr = new GlideRecord("x_snc_donow_context");
		gr.addQuery("owner", gs.getUserID());
		gr.addQuery("my_default", true);
		gr.query();
		if (gr.next()) {
			return gr.getValue('value');
		}

		return;

	}, 

	type: 'DoNowUtil'
};
