sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	var dnu = new DoNowUtil();
	var user = dnu.getGTDUser(email.recipients);
	var context = dnu.parseContext(email.subject, user);
	var priority = dnu.parsePriority(email.subject);
	gs.debug("in inbound action, user = " + user + " context = " + context + " priority = " + priority);
	current.more_information = email.body_text;
	current.active = true;

	current.short_description = dnu.sanitizeSubject(email.subject);
	current.assigned_to = user;
	current.opened_by = user;
	current.context = context;
	current.priority = priority;
	current.insert();

})(current, event, email, logger, classifier);
